import React, { useState, useEffect } from 'react';
import { scanTextFiles, getFileInfo } from '../StoryTxt/StoryService/FileScannerService.js';
import './Stories.css';

const Stories = () => {
  const [stories, setStories] = useState([]);
  const [loading, setLoading] = useState(true);
  const [error, setError] = useState(null);
  const [selectedStory, setSelectedStory] = useState(null);

  useEffect(() => {
    loadStories();
  }, []);

  const loadStories = async () => {
    try {
      setLoading(true);
      setError(null);

      // Scan for text files in the StoryTxt folder
      const scanResult = await scanTextFiles('./src/StoryTxt/');

      if (scanResult.success) {
        // Get detailed information for each file
        const detailedStories = await Promise.all(
          scanResult.data.files.map(async (file) => {
            const fileInfo = await getFileInfo(file.path);
            return fileInfo.success ? fileInfo.data : null;
          })
        );

        // Filter out any failed file reads
        const validStories = detailedStories.filter(story => story !== null);
        setStories(validStories);
      } else {
        setError(scanResult.error.message);
      }
    } catch (err) {
      setError(err.message);
    } finally {
      setLoading(false);
    }
  };

  const handleStoryClick = (story) => {
    setSelectedStory(story);
  };

  const handleCloseStory = () => {
    setSelectedStory(null);
  };

  if (loading) {
    return (
      <div className="stories-container">
        <div className="loading-spinner">
          <div className="spinner"></div>
          <p>Loading stories...</p>
        </div>
      </div>
    );
  }

  if (error) {
    return (
      <div className="stories-container">
        <div className="error-message">
          <h2>Error Loading Stories</h2>
          <p>{error}</p>
          <button onClick={loadStories} className="retry-button">
            Try Again
          </button>
        </div>
      </div>
    );
  }

  return (
    <div className="stories-container">
      <div className="stories-grid">
        {stories.map((story, index) => (
          <div 
            key={index} 
            className="story-card"
            onClick={() => handleStoryClick(story)}
          >
            <div className="story-card-header">
              <h3>{story.title}</h3>
              <div className="story-stats">
                <span className="stat">{story.lineCount} lines</span>
                <span className="stat">{story.wordCount} words</span>
              </div>
            </div>
            <div className="story-card-content">
              <p className="story-info">{story.info}</p>
              <p className="story-preview">
                {story.firstLine.length > 100 
                  ? `${story.firstLine.substring(0, 100)}...` 
                  : story.firstLine}
              </p>
            </div>
            <div className="story-card-footer">
              <span className="read-more">Click to read →</span>
            </div>
          </div>
        ))}
      </div>

      {/* Story Modal */}
      {selectedStory && (
        <div className="story-modal-overlay" onClick={handleCloseStory}>
          <div className="story-modal" onClick={(e) => e.stopPropagation()}>
            <div className="story-modal-header">
              <h2>{selectedStory.title}</h2>
              <button className="close-button" onClick={handleCloseStory}>
                ×
              </button>
            </div>
            <div className="story-modal-content">
              <div className="story-metadata">
                <span className="metadata-item">{selectedStory.lineCount} lines</span>
                <span className="metadata-item">{selectedStory.wordCount} words</span>
                <span className="metadata-item">{selectedStory.characterCount} characters</span>
              </div>
              <div className="story-text">
                {selectedStory.content.split('\n').map((line, index) => (
                  <p key={index}>{line}</p>
                ))}
              </div>
            </div>
          </div>
        </div>
      )}
    </div>
  );
};

export default Stories;
