import React, { useState, useEffect } from 'react';
import { scanTextFiles, getFileInfo } from '../StoryTxt/StoryService/FileScannerService.js';

const Stories = () => {
  const [stories, setStories] = useState([]);
  const [loading, setLoading] = useState(true);
  const [error, setError] = useState(null);
  const [selectedStory, setSelectedStory] = useState(null);

  useEffect(() => {
    loadStories();
  }, []);

  const loadStories = async () => {
    try {
      setLoading(true);
      setError(null);

      // Scan for text files in the StoryTxt folder
      const scanResult = await scanTextFiles('./src/StoryTxt/');

      if (scanResult.success) {
        // Get detailed information for each file
        const detailedStories = await Promise.all(
          scanResult.data.files.map(async (file) => {
            const fileInfo = await getFileInfo(file.path);
            return fileInfo.success ? fileInfo.data : null;
          })
        );

        // Filter out any failed file reads
        const validStories = detailedStories.filter(story => story !== null);
        setStories(validStories);
      } else {
        setError(scanResult.error.message);
      }
    } catch (err) {
      setError(err.message);
    } finally {
      setLoading(false);
    }
  };

  const handleStoryClick = (story) => {
    setSelectedStory(story);
  };

  const handleCloseStory = () => {
    setSelectedStory(null);
  };

  if (loading) {
    return (
      <div className="stories-container">
        <div className="loading-terminal">
          <div className="terminal-line">INITIALIZING STORY DATABASE...</div>
          <div className="terminal-line">SCANNING FILESYSTEM...</div>
          <div className="terminal-line">LOADING STORIES...</div>
          <div className="terminal-cursor">█</div>
        </div>
      </div>
    );
  }

  if (error) {
    return (
      <div className="stories-container">
        <div className="error-terminal">
          <div className="terminal-line">ERROR: {error}</div>
          <div className="terminal-line">SYSTEM FAILURE DETECTED</div>
          <button onClick={loadStories} className="retry-terminal">
            RETRY CONNECTION
          </button>
        </div>
      </div>
    );
  }

  return (
    <div className="stories-container">
      <div className="page-heading">
        <h1>Stories</h1>
      </div>
      <div className="stories-grid">
        {stories.map((story, index) => (
          <div 
            key={index} 
            className="story-terminal"
            onClick={() => handleStoryClick(story)}
          >
            <div className="terminal-header">
              <span className="file-path">/{story.fileName}.txt</span>
              <span className="file-size">{story.fileSize} bytes</span>
            </div>
            <div className="terminal-content">
              <div className="story-title">{story.title}</div>
              <div className="story-stats">
                <span className="stat">LINES: {story.lineCount}</span>
                <span className="stat">WORDS: {story.wordCount}</span>
              </div>
              <div className="story-preview">
                {story.firstLine.length > 80 
                  ? `${story.firstLine.substring(0, 80)}...` 
                  : story.firstLine}
              </div>
            </div>
            <div className="terminal-footer">
              <span className="access-code">ACCESS_READ</span>
            </div>
          </div>
        ))}
      </div>

      {/* Story Terminal Modal */}
      {selectedStory && (
        <div className="terminal-modal-overlay" onClick={handleCloseStory}>
          <div className="terminal-modal" onClick={(e) => e.stopPropagation()}>
            <div className="modal-header">
              <div className="modal-title">
                <span className="file-path">/{selectedStory.fileName}.txt</span>
                <span className="file-info">SIZE: {selectedStory.fileSize} bytes</span>
              </div>
              <button className="close-terminal" onClick={handleCloseStory}>
                [X]
              </button>
            </div>
            <div className="modal-content">
              <div className="story-metadata">
                <div className="metadata-line">LINES: {selectedStory.lineCount}</div>
                <div className="metadata-line">WORDS: {selectedStory.wordCount}</div>
                <div className="metadata-line">CHARACTERS: {selectedStory.characterCount}</div>
              </div>
              <div className="story-text">
                {selectedStory.content.split('\n').map((line, index) => (
                  <div key={index} className="text-line">{line}</div>
                ))}
              </div>
            </div>
          </div>
        </div>
      )}
    </div>
  );
};

export default Stories;
