import React from 'react';

const Games = () => {
  const gameProjects = [
    {
      id: 1,
      title: "Cosmic Escape",
      status: "IN DEVELOPMENT",
      description: "A survival horror game set in the depths of space where players must navigate through abandoned space stations while avoiding cosmic entities.",
      features: ["Procedural Generation", "Atmospheric Horror", "Space Exploration"],
      fileName: "cosmic-escape",
      fileSize: "2.4 MB"
    },
    {
      id: 2,
      title: "Void Runner",
      status: "PLANNING",
      description: "A fast-paced action game where players race through dimensional rifts, collecting cosmic energy while avoiding the void's corruption.",
      features: ["High-Speed Gameplay", "Dimensional Mechanics", "Energy Collection"],
      fileName: "void-runner",
      fileSize: "1.8 MB"
    },
    {
      id: 3,
      title: "Nebula's Edge",
      status: "CONCEPT",
      description: "An exploration-based adventure game where players discover ancient alien civilizations and uncover the secrets of the Verse of the Sun.",
      features: ["Story-Driven", "Exploration", "Alien Civilizations"],
      fileName: "nebula-edge",
      fileSize: "3.1 MB"
    }
  ];

  return (
    <div className="stories-container">
      <div className="stories-page-heading">
        <h1>Games Terminal</h1>
      </div>
      <div className="stories-grid">
        {gameProjects.map((game, index) => (
          <div 
            key={game.id} 
            className="story-terminal"
          >
            <div className="terminal-header">
              <span className="file-path">/{game.fileName}.game</span>
              <span className="file-size">{game.fileSize}</span>
            </div>
            <div className="terminal-content">
              <div className="story-title">{game.title}</div>
              <div className="story-stats">
                <span className="stat">STATUS: {game.status}</span>
                <span className="stat">TYPE: ACTION</span>
              </div>
              <div className="story-preview">
                {game.description}
              </div>
            </div>
            <div className="terminal-footer">
              <span className="access-code">ACCESS_DEV</span>
            </div>
          </div>
        ))}
      </div>
    </div>
  );
};

export default Games;
