import { useState, useEffect } from 'react';
import { BrowserRouter, Routes, Route, useLocation } from 'react-router';
import logo from './logo.svg';
import Header from './Components/Header';
import Home from './Pages/Home';
import Stories from './Pages/Stories';
import Games from './Pages/Games';
import './App.css';
import Footer from './Components/Footer';
import blackhole from './Images/blackhole.svg';

function BlackholeOverlay({ show }) {
  if (!show) return null;
  return (
    <div className="blackhole-overlay">
      <img src={blackhole} alt="Blackhole" className="blackhole-img" />
    </div>
  );
}

function AppRoutesWithTransition() {
  const location = useLocation();
  const [showBlackhole, setShowBlackhole] = useState(false);

  useEffect(() => {
    setShowBlackhole(true);
    const timer = setTimeout(() => setShowBlackhole(false), 500);
    return () => clearTimeout(timer);
  }, [location]);

  return (
    <>
      <BlackholeOverlay show={showBlackhole} />
      <div className="App">
        <Routes location={location}>
          <Route path="/" element={<Home />} />
          <Route path="/stories" element={<Stories />} />
          <Route path="/games" element={<Games />} />
        </Routes>
      </div>
    </>
  );
}

function App() {
  const [isLoading, setIsLoading] = useState(true);
  useEffect(() => {
    const timer = setTimeout(() => setIsLoading(false), 1500);
    return () => clearTimeout(timer);
  }, []);

  return (
    isLoading ? (
      <div className="loading">
        <h1>Loading...</h1>
        <img src={logo} alt="logo" />
      </div>
    ) : (
      <div className="main-container">
        <BrowserRouter>
          <Header/>
          <AppRoutesWithTransition />
          <Footer />
        </BrowserRouter>
      </div>
    )
  );
}

export default App;
